class Solution {
 public:
  int minFlips(vector<vector<int>>& a) {
    int n = a.size(), m = a[0].size(), ans = 0;

    for (int i1 = 0, i2 = n - 1; i1 < i2; i1++, i2--) {
      for (int j1 = 0, j2 = m - 1; j1 < j2; j1++, j2--) {
        int one = a[i1][j1] + a[i1][j2] + a[i2][j1] + a[i2][j2];
        ans += std::min(one, 4 - one);
      }
    }
    int zero_zero = 0;
    int zero_one = 0;
    int one_one = 0;
    if (n % 2 == 1) {
      for (int i1 = 0, i2 = m - 1; i1 < i2; i1++, i2--) {
        if (a[n / 2][i1] == 1 && a[n / 2][i2] == 1) {
          one_one++;
        } else if (a[n / 2][i1] == 0 && a[n / 2][i2] == 0) {
          zero_zero++;
        } else {
          zero_one++;
        }
      }
    }
    if (m % 2 == 1) {
      for (int i1 = 0, i2 = n - 1; i1 < i2; i1++, i2--) {
        if (a[i1][m / 2] == 1 && a[i2][m / 2] == 1) {
          one_one++;
        } else if (a[i1][m / 2] == 0 && a[i2][m / 2] == 0) {
          zero_zero++;
        } else {
          zero_one++;
        }
      }
    }
    if (zero_one == 0 && one_one % 2 == 1) {
      ans += 2;
    } else {
      ans += zero_one;
    }
    if (n % 2 == 1 && m % 2 == 1 && a[n / 2][m / 2] == 1) {
      ans++;
    }
    return ans;
  }
};
