class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int cnt = 0, res = 0, mid2 = 0;

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                //cout << i << " " << j << endl;
                int topLeft = grid[i][j];
                int topRight = grid[i][m - 1 - j];
                int bottomLeft = grid[n - 1 - i][j];
                int bottomRight = grid[n - 1 - i][m - 1 - j];
                int sum = topLeft + topRight + bottomLeft + bottomRight;
                //cout << sum << endl;
                cnt += min(sum, 4 - sum);
            }
        }
        if (n % 2 == 1) {
            int midRow = n / 2;
            //cout << midRow << endl;
            for (int j = 0; j < m / 2; ++j) {
                if (grid[midRow][j] != grid[midRow][m - 1 - j]) {
                    ++cnt;
                    ++mid2;
                } else {
                    if (grid[midRow][j] == 0 && grid[midRow][m - 1 - j] == 0) continue;
                    res += 2;
                }
            }
        }
        //cout << cnt << endl;
        if (m % 2 == 1) {
            int midCol = m / 2;
            //cout << midCol << endl;
            for (int i = 0; i < n / 2; ++i) {
                if (grid[i][midCol] != grid[n - 1 - i][midCol]) {
                    ++cnt, ++mid2;
                }
                else {
                    if (grid[i][midCol] == 0 && grid[n - 1 - i][midCol] == 0) continue;
                    res += 2;
                }
            }
        }
        if (n % 2 == 1 && m % 2 == 1) {
            //cout << n / 2 << " " << m / 2;
            if (grid[n / 2][m / 2] == 1) ++cnt;
        }
        //cout << res;
        //cout << cnt;
        if (res % 4 != 0) {
            //cout <<cnt << endl;
            //cout << mid2 << endl;
            if (mid2 == 0) cnt += 2;
    }
        return cnt;
    }
};
