class Solution {
    public int minFlips(int[][] grid) {
        int m=grid.length;
        int n=grid[0].length;
        int mmid=m/2;
        int nmid=n/2;
        int moves=0;
        for(int i=0;i<mmid;i++){
            for(int j=0;j<nmid;j++){
                int freq[]=new int[2];
                freq[grid[i][j]]++;
                freq[grid[m-i-1][j]]++;
                freq[grid[i][n-1-j]]++;
                freq[grid[m-i-1][n-1-j]]++;
                // System.out.println(Arrays.toString(freq));
                moves+=Math.min(freq[0],freq[1]);
            }
        }
        int ones=0;
        int changed=0;
        if(m%2==1){
            int l=0;
            int r=n-1;
            while(l<r){
                if(grid[mmid][l]!=grid[mmid][r]){
                    changed++;
                    moves++;
                    grid[mmid][l]=1;
                    grid[mmid][r]=1;
                }
                ones+=grid[mmid][l++];
                ones+=grid[mmid][r--];
            }
        }
        if(n%2==1){
            int l=0;
            int r=m-1;
            while(l<r){
                if(grid[l][nmid]!=grid[r][nmid]){
                    changed++;
                    moves++;
                    grid[l][nmid]=1;
                    grid[r][nmid]=1;
                }
                ones+=grid[l++][nmid];
                ones+=grid[r--][nmid];
            }
        }
        if(n%2==1&&m%2==1&&grid[mmid][nmid]==1){
            moves++;
        }
        ones%=4;
        if(ones==2&&changed==0){
            moves+=2;
        }
        return moves;
    }
}
