class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans= 0 , cnt = 0;
        int n = grid.size() , m = grid[0].size() , ones = 0;
        for(int i = 0; i < n/2 ; i++){
            for(int j = 0 ; j < m / 2 ; j++){
                int toZero = grid[i][j] + grid[i][m - 1 - j] + grid[n - 1 -i][j] + grid[n - 1 - i][m - 1 -j] , toOne = 4 - toZero;
                if(toZero < toOne){
                    ans += toZero;
                }
                else{
                    ans += toOne;
                }
            }
        }
        if(n%2 != 0){
            int i = n/2;
            for(int j = 0 ; j < m / 2 ; j++){
                int toZero = grid[i][j] + grid[i][m - 1 - j], toOne = 2 - toZero;
                if(toZero == 1)cnt++;
                if(toZero < toOne){
                    ans += toZero;
                }
                else{
                    ans += toOne;
                    ones += 2;
                }
            }
        }
        if(m % 2 == 1){
            int j = m/2 ;
            for(int i = 0 ; i < n /2 ; i++){
                int toZero = grid[i][j] + grid[n - 1 -i][j] , toOne = 2 - toZero;
                if(toZero == 1)cnt++;
                if(toZero < toOne){
                    ans += toZero;
                }
                else{
                    ans += toOne;
                    ones += 2;
                }
            }
        }
        if(m%2 == 1 && n % 2 ==1)ans += grid[n/2][m/2];
        if(cnt >= 1 || ones % 4 == 0)return ans;
        return ans + 2;
    }
};
