class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int temp=grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                ans+=min(temp,4-temp);
            }
        }
        int temp=0,cnt=0;
        if(n%2==1)
        {

            for(int i=0;i<m/2;i++)
            {
              if(grid[n/2][i]!=grid[n/2][m-i-1])
              {
                temp++;
              }
              else if(grid[n/2][i]==grid[n/2][m-i-1] && grid[n/2][i]==1)
              {
                cnt++;
              }
            }
        }
        if(m%2==1)
        {
            for(int i=0;i<n/2;i++)
            {
              if(grid[i][m/2]!=grid[n-i-1][m/2])
              {
                temp++;
              }
              else if(grid[i][m/2]==grid[n-i-1][m/2] && grid[i][m/2]==1)
              {
                cnt++;
              }
            }
        }
        if(cnt%2==0)
            ans+=temp;
            else
            {
                if(temp)
                {
                    ans+=temp;
                }
                else
                ans+=2;
            }
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1)
        ans++;
        return ans;
    }
};
