class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        g = grid
        m,n = len(g), len(g[0])

        hm = m//2
        hn = n//2

        ans = 0

        for i in range(hm):
            for j in range(hn):
                c = g[i][j]+g[m-1-i][j]+g[i][n-1-j]+g[m-1-i][n-1-j]
                ans+=min(c, 4-c)

        c01 = 0
        c00 = 0
        c11 = 0

        if m%2:
            for j in range(hn):
                c = g[hm][j]+g[hm][n-1-j]
                if c==1:
                    c01+=1
                if c==2:
                    c11+=1
                if c==0:
                    c00+=1

        if n%2:
            for i in range(hm):
                c = g[i][hn]+g[m-1-i][hn]
                if c==1:
                    c01+=1
                if c==2:
                    c11+=1
                if c==0:
                    c00+=1

        ans+=c01

        if c11%2:
            if c01==0:
                ans+=2

        if m%2 and n%2 and g[hm][hn]:
            ans+=1

        return ans
