class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();

        int ans=0;

        for(int i=0;i<(n/2);i++)
        {
            for(int j=0;j<(m/2);j++)
            {
                int ni=n-i-1;
                int nj=m-j-1;

                int cnt=grid[i][j]+grid[i][nj]+grid[ni][j]+grid[ni][nj];
                ans+=min(cnt,4-cnt);
            }
        }

        int co=0,vo=0;
        int cc=0;
        if(m%2)
        {
            for(int i=0;i<(n/2);i++)
            {
                if(grid[i][m/2]!=grid[n-i-1][m/2]) vo++;
                else if(grid[i][m/2] == 1) co+=2;
            }
        }
        if(n%2)
        {
            for(int j=0;j<(m/2);j++)
            {
                if(grid[n/2][j]!=grid[n/2][m-j-1]) vo++;
                else if(grid[n/2][j] == 1) co+=2;
            }
        }

        if((m%2)==1 && (n%2)==1) cc=grid[n/2][m/2];

        // cout<<vo<<" "<<co<<" "<<cc<<'\n';

        ans+=vo;
        if(vo==0)
        {
            ans+=(cc+co)%4;
        }        
        else
        {
            int rem=(cc+co)%4;
            if(rem==3 || rem==1) ans++;
        }
        return ans;
    }
};
