class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int res=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int count=grid[i][j]+grid[m-i-1][j]+grid[i][n-j-1]+grid[m-i-1][n-j-1];
                if(count==1 || count==3)res++;
                else if(count==2)res+=2;
            }
        }
        //cout << res;
        int count1=0;
        int count2=0;
        if(m%2==1){
            int k=m/2;
            for(int j=0;j<n/2;j++){
                if(grid[k][j]==1 && grid[k][n-j-1]==1)count1+=2;
                if(grid[k][j]!=grid[k][n-j-1])count2++;
            }
            //if(n%2==1 && grid[k][n/2]==1)count1-=2;
        }
        if(n%2==1){
            int k=n/2;
            for(int i=0;i<m/2;i++){
                if(grid[i][k]==1 && grid[m-i-1][k]==1)count1+=2;
                if(grid[i][k]!=grid[m-i-1][k])count2++;
            }
            //if(m%2==1 && grid[m/2][k]==1)count1-=2;
        }
        if(count2>0)res+=count2;
        if(count1%4==2 && count2==0){
            res+=2;
        }
        if(m%2==1 && n%2==1 && grid[m/2][n/2]==1)res++;
        return res;
    }
};
