class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int ans=0,n=v.size(),m=v[0].size();
        if(n%2&&m%2){
            ans+=v[n/2][m/2];
        }

        for(int i=0;i<n&&i<n-i-1;++i){
            for(int j=0;j<m&&j<m-j-1;++j){
                int nr1=v[i][j]+v[n-i-1][j]+v[i][m-j-1]+v[n-i-1][m-j-1];
                ans+=min(nr1,4-nr1);
            }
        }

        int nr0=0,nr1=0;
        if(n%2){
            for(int j=0;j<m&&j<m-j-1;++j){
                if(v[n/2][j]!=v[n/2][m-j-1]){
                    ++nr0;
                    ++ans;
                }
                else if(v[n/2][j]==1){
                    ++nr1;
                }
            }
        }

        if(m%2){
            for(int i=0;i<n&&i<n-i-1;++i){
                if(v[i][m/2]!=v[n-i-1][m/2]){
                    ++nr0;
                    ++ans;
                }
                else if(v[i][m/2]==1){
                    ++nr1;
                }
            }
        }

        if((nr0+nr1)%2){
            if(!nr0){
                ans+=2;
            }
        }

        return ans;
    }
};
