class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        int otoz4 = 0;
        int ocount = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int z = 0;
                int o = 0;
                if(grid[i][j]==0){
                    z++;
                }
                else{
                    o++;
                }
                if(grid[i][m-j-1]==0){
                    z++;
                }
                else{
                    o++;
                }
                if(grid[n-i-1][j]==0){
                    z++;
                }
                else{
                    o++;
                }
                if(grid[n-i-1][m-j-1]==0){
                    z++;
                }
                else{
                    o++;
                }
                ans+=min(z,o);
                if(z==o){
                    otoz4++;
                }
                if(o>z){
                    ocount+=4;
                }
            }
        }
        int otoz2 = 0;
        int one = 0;
        if(m%2==1){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-i-1][m/2]){
                    ans++;
                    otoz2++;
                }
                else{
                    if(grid[i][m/2]==1){
                        one++;
                    }
                }
            }
        }
        if(n%2==1){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j] != grid[n/2][m-j-1]){
                    ans++;
                    otoz2++;
                }
                else{
                    if(grid[n/2][j]==1){
                        one++;
                    }
                }
            }
        }
        if(one%2==1){
            if(otoz2==0){
                ans+=2;
            }
        }
        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]==1){
                ans++;
            }
        }
        return ans;

    }
};
