class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ss=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int i1=n-i-1,j1=m-j-1;
                int sum=grid[i][j]+grid[i][j1]+grid[i1][j]+grid[i1][j1];
                if(sum==0 || sum==4)
                    continue;
                if(sum==1 || sum==3)
                    ss+=1;
                else
                {
                    ss+=2;
                }
            }
        }
        if(m%2==0 && n%2==0)
            return ss;
        if(m%2 && n%2==0)
        {
            int a=0;
            int num1=0;
            int j=m/2;
            for(int i=0;i<n/2;i++)
            {
                if(grid[i][j]!=grid[n-i-1][j])
                {
                    a++;
                }
                else if(grid[i][j]==1)
                    num1+=2;
            }
            if(num1%4==0)
                return ss+a;
            else
            {
                if(a>0)
                {
                    return ss+a;
                }
                else
                    return ss+2;
            }
        }
        if(m%2==0 && n%2)
        {
            int a=0;
            int num1=0;
            int i=n/2;
            for(int j=0;j<m/2;j++)
            {
                if(grid[i][j]!=grid[i][m-j-1])
                {
                    a++;
                }
                else if(grid[i][j]==1)
                    num1+=2;
            }
            if(num1%4==0)
                return ss+a;
            else
            {
                if(a>0)
                {
                    return ss+a;
                }
                else
                    return ss+2;
            }
        }
        if(m%2 && n%2)
        {
            int a=0;
            int num1=0;
            int i=n/2;
            for(int j=0;j<m/2;j++)
            {
                if(grid[i][j]!=grid[i][m-j-1])
                {
                    a++;
                }
                else if(grid[i][j]==1)
                    num1+=2;
            }

            int j=m/2;
            for(i=0;i<n/2;i++)
            {
                if(grid[i][j]!=grid[n-i-1][j])
                {
                    a++;
                }
                else if(grid[i][j]==1)
                    num1+=2;
            }
            if(grid[n/2][m/2]==0)
            {
                if(num1%4==0)
                    return ss+a;
                else
                    if(a>0)
                        return ss+a;
                    else
                        return ss+2;

            }
            else
            {
                if(num1%4==0)
                    return ss+a+1;
                else
                    if(a>0)
                        return ss+a+1;
                    else
                        return ss+3;
            }

        }
        return 0;
    }
};
