#define pb push_back
typedef long long ll;
ll dp[1000000][4];
class Solution {
public:

    vector<vector<int>>des;
    ll solve(int in, int cur)
    {
        if (in == des.size())
        {
            if ((cur % 4) == 0)return 0;
            return 1e6;
        }
        if (dp[in][cur] != -1)return dp[in][cur];
        ll zero = des[in][2] + solve(in + 1, cur);
        ll one = des[in][1] + solve(in + 1, (cur + des[in][0]) % 4);
        return dp[in][cur] = min(zero, one);
    }
    int minFlips(vector<vector<int>>& grid) {
        vector<vector<int>>arr = grid;
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;

        int c = 0;
        map<pair<int,int>,int>done;
        for (int i = 0; i < (n + 1) / 2; i++)
        {
            for (int j = 0; j < (m + 1) / 2; j++)
            {
                // if(i==1 and j==1)cout<<i<<" "<<j<<endl;
                int one = 0, zero = 0;
                vector<pair<int, int>>dir;
                dir.push_back({i, j});
                dir.push_back({i, m - 1 - j}); dir.push_back({n - 1 - i, j});
                dir.push_back({n - 1 - i, m - 1 - j});
                int o = 0;
                int tot = 0;
                vector<int>tell;
                int z = 0;
                // set<pair<int,int>>done;

                for (auto k : dir)
                {
                    if ((k.first >= 0 )and (k.first < n) and (k.second >= 0) and (k.second < m) and !done[{k.first,k.second}])
                    {

                        if (grid[k.first][k.second] == 1)
                        {
                            o++;
                        }
                        else z++;
                        tot++;
                        // done.insert({i,j});
                        done[{k.first,k.second}]=1;
                    }
                }
                // if(i==1 and j==1)cout<<tot<<endl;
                tell.pb(tot);
                tell.pb(tot - o);
                tell.pb(tot - z);
                // int z = tot - o;
                des.pb(tell);
                for (int wow = 0; wow < 4; wow++)
                {
                    dp[c][wow] = -1;
                }
                c++;


            }
        }
        // for (auto i : des)
        // {   for (auto j : i)cout << j << " ";
            // cout << endl;
        // }
        return solve(0, 0);
    }
};
