class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n=grid.size(),m=grid[0].size();
        int ui=0,li=n-1;
        while(ui<li){
            int uj=0,lj=m-1;
            while(uj<lj){
                int temp=grid[ui][uj]+grid[ui][lj]+grid[li][lj]+grid[li][uj];
                if(temp==1||temp==3)ans+=1;
                else if(temp==2)ans+=2;
                uj++;
                lj--;
            }
            ui++;
            li--;
        }
        int temp=0,one=0;
        if(n%2){
            int i=0,j=m-1,k=n/2;
            while(i<j){
                if(grid[k][i]!=grid[k][j])temp++;
                else{
                    if(grid[k][i]==1)one++;
                }
                i++;
                j--;
            }
        }
        if(m%2){
            int i=0,j=n-1,k=m/2;
            while(i<j){
                if(grid[i][k]!=grid[j][k])temp++;
                else{
                    if(grid[i][k]==1)one++;
                }
                i++;
                j--;
            }
        }
        if(n%2&&m%2){
            if(grid[n/2][m/2])ans++;
        }
        one=one%2;
        if(one==0)return (ans+temp);
        else{
            if(temp>0)return (ans+temp);
            else return (ans+2);
        }
    }
};
