class Solution {
    vector<vector<int>>adj;
    vector<int>maxi;
    vector<int>secondMaxi;
    void precompute(int node,int par){
        for(auto it:adj[node]){
            if(it!=par){
                precompute(it,node);
                int thisTime = maxi[it]+(it%2==0?2:1);
                if(thisTime>maxi[node]){
                    secondMaxi[node] = maxi[node];
                    maxi[node] = thisTime;
                }else if(thisTime>secondMaxi[node]){
                    secondMaxi[node] = thisTime;
                }
            }
        }
    }


    void dfs(int node,int par){
        for(auto it:adj[node]){
            if(it!=par){
                int thisContri = maxi[it]+(it%2==0?2:1);
                int parContri = maxi[node]+(node%2==0?2:1);
                if(thisContri==maxi[node]) parContri = secondMaxi[node]+(node%2==0?2:1);
                if(parContri>maxi[it]){
                    secondMaxi[it] = maxi[it];
                    maxi[it] = parContri;
                }else if(parContri>secondMaxi[it]){
                    secondMaxi[it] = parContri;
                }
                dfs(it,node);
            }
        }
    }
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size()+1;
        adj.resize(n);
        maxi.resize(n,0);
        secondMaxi.resize(n,0);
        for(auto it:edges){
            adj[it[0]].push_back(it[1]);
            adj[it[1]].push_back(it[0]);
        }
        precompute(0,-1);
        dfs(0,-1);
        return maxi;
    }
};
