class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& es) {
        int n=es.size()+1;
        vector<vector<int>> e(n);
        vector<int> f(n,0);
        vector<int> g(n,0);
        for(auto &v:es){
            auto x=v[0];
            auto y=v[1];
            e[x].push_back(y);
            e[y].push_back(x);
        }
        auto dfs=[&](auto &&dfs,int x,int fa)->void{
            for(auto v:e[x]){
                if(v==fa)
                    continue;
                dfs(dfs,v,x);
                g[x]=max(g[x],f[v]+2-v%2);
                if(g[x]>f[x])
                    swap(f[x],g[x]);
            }
        };
        dfs(dfs,0,-1);
        auto dfs2=[&](auto &&dfs2,int x,int fa)->void{
            for(auto v:e[x]){
                if(v==fa)
                    continue;
                int tmp=f[v]+2-v%2;
                int fk=tmp==f[x]?g[x]:f[x];
                g[v]=max(g[v],fk+2-x%2);
                if(g[v]>f[v])
                    swap(g[v],f[v]);
                dfs2(dfs2,v,x);
            }
        };
        dfs2(dfs2,0,-1);
        return f;
    }
};
