class Solution:
    def timeTaken(self, edges: List[List[int]]) -> List[int]:
        n=len(edges)+1
        g=[[] for _ in range(n)]
        for u,v in edges:
            g[u]+=[v]
            g[v]+=[u]
        h=[0]*n  #标记了i点后，多少秒i子树全部被标记
        def dfs(u,fa):
            for v in g[u]:
                if v==fa:
                    continue
                dfs(v,u)
                if v%2==1:
                    h[u]=max(h[u],h[v]+1)
                else:
                    h[u]=max(h[u],h[v]+2)    
        dfs(0,-1)
        res=[0]*n
        def dfs2(u,fa):
            fi=se=0
            for v in g[u]:
                if v%2==1:
                    t=h[v]+1
                else:
                    t=h[v]+2
                if t>fi:
                    fi,se=t,fi
                elif t>se:
                    se=t
            res[u]=fi
            for v in g[u]:
                if v==fa:
                    continue
                if v%2==1:
                    t=h[v]+1
                else:
                    t=h[v]+2
                if t==fi:
                    h[u]=se
                else:
                    h[u]=fi
                dfs2(v,u)
        dfs2(0,-1)
        return res
