class Solution {
    int getmx(multiset<int>&mst)
    {
        auto it=mst.end();
        it--;
        return *it;
    }
    int dfs(int node,int par,vector<vector<int>>&adj,vector<multiset<int>>&dp)
    {
        dp[node].insert(0);
        for(auto i:adj[node])
        {
            if(i==par) continue;
            int temp=dfs(i,node,adj,dp);
            if(i%2==0) temp+=2;
            else temp+=1;
            dp[node].insert(temp);
        }
        return getmx(dp[node]);
    }
    void rerooting(int node,int par,vector<vector<int>>&adj,vector<multiset<int>>&dp,vector<int>&ans)
    {
        ans[node]=getmx(dp[node]);
        for(auto i:adj[node])
        {
            if(i==par) continue;
            int p=getmx(dp[i]);
            if(i%2==0) p+=2;
            else p+=1;
            dp[node].erase(dp[node].find(p));
            int q=getmx(dp[node]);
            if(node%2==0) q+=2;
            else q+=1;
            dp[i].insert(q);
            rerooting(i,node,adj,dp,ans);
            dp[i].erase(dp[i].find(q));
            dp[node].insert(p);
        }
    }
public:
    vector<int> timeTaken(vector<vector<int>>& ed) {
        int n=ed.size()+1;
        vector<multiset<int>>dp(n);
        vector<vector<int>>adj(n);
        for(auto i:ed)
        {
            adj[i[0]].push_back(i[1]);
            adj[i[1]].push_back(i[0]);
        }
        dfs(0,-1,adj,dp);
        vector<int>ans(n);
        rerooting(0,-1,adj,dp,ans);
        return ans;
    }
};
