const int N=1e5;
vector <int> a[N+1];
int dp[N+1][2];
int child[N+1];
class Solution {
public:
    void dfs(int ind,int p)
    {
        dp[ind][0]=0,dp[ind][1]=0;
        for (auto x:a[ind]){
            if (x!=p){
                dfs(x,ind);
                int f=1;
                if (x%2==0) f++;
                if (f+dp[x][0]>dp[ind][0]){
                    dp[ind][1]=dp[ind][0];
                    dp[ind][0]=f+dp[x][0];
                    child[ind]=x;
                }
                else if (f+dp[x][0]>dp[ind][1]){
                    dp[ind][1]=f+dp[x][0];
                }
            }
        }
    }
    void dfs1(int ind,int p)
    {
        for (auto x:a[ind]){
            if (x!=p){
                int f=1;
                if (ind%2==0) f++;
                if (child[ind]==x){
                    if (f+dp[ind][1]>dp[x][0]){
                        dp[x][1]=dp[x][0];
                        dp[x][0]=f+dp[ind][1];
                        child[x]=ind;
                    }
                    else if (f+dp[ind][1]>dp[x][1]){
                        dp[x][1]=f+dp[ind][1];
                    }
                }
                else{
                    if (f+dp[ind][0]>dp[x][0]){
                        dp[x][1]=dp[x][0];
                        dp[x][0]=f+dp[ind][0];
                        child[x]=ind;
                    }
                    else if (f+dp[ind][0]>dp[x][1]){
                        dp[x][1]=f+dp[ind][0];
                    }
                }
                dfs1(x,ind);
            }
        }
    }
    vector<int> timeTaken(vector<vector<int>>& e) {
        int n=e.size()+1;
        for (int i=0;i<n;i++) a[i].clear(),dp[i][0]=0,dp[i][1]=0,child[i]=-1;
        for (auto x:e){
            a[x[0]].push_back(x[1]);
            a[x[1]].push_back(x[0]);
        }
        dfs(0,-1);
       // for (int i=0;i<n;i++) cout<<dp[i][0]<<" "<<dp[i][1]<<"\n";
        dfs1(0,-1);
        //for (int i=0;i<n;i++) cout<<dp[i][0]<<" "<<dp[i][1]<<"\n";
        vector <int> ans(n);
        for (int i=0;i<n;i++){
            ans[i]=dp[i][0];
        }
        return ans;
    }
};
