class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edge) {
        int n = (int)edge.size() + 1;
        vector<vector<int>> edges(n);
        for(auto i : edge){
            int u = i[0],v = i[1];
            edges[u].push_back(v);
            edges[v].push_back(u);
        }
        vector<int> mx(n);
        auto dfs = [&](auto &dfs,int x,int p = -1) -> void {
            for(auto y : edges[x]){
                if(y == p) continue;
                dfs(dfs,y,x);
                mx[x] = max(mx[x],mx[y] + (y % 2 == 0 ? 2 : 1));
            }
        };
        dfs(dfs,0);
        vector<int> dp(n);
        auto ddfs = [&](auto &ddfs,int x,int p = -1,int up = 0) -> void {
            dp[x] = max(up,mx[x]);
            vector<pair<int,int>> pos;
            pos.push_back({up,x});
            for(auto y : edges[x]){
                if(y == p) continue;
                pos.push_back({mx[y] + (y % 2 == 0 ? 2 : 1), y});
            }
            sort(pos.rbegin(),pos.rend());
            for(auto y : edges[x]){
                if(y == p) continue;
                if(pos.front().second == y){
                    ddfs(ddfs,y,x,pos[1].first + (x % 2 == 0 ? 2 : 1));
                } else {
                    ddfs(ddfs,y,x,pos[0].first + (x % 2 == 0 ? 2 : 1));
                }
            }
        };
        ddfs(ddfs,0);
        return dp;
    }
};
