class Solution:
    def timeTaken(self, e: List[List[int]]) -> List[int]:
        n = len(e) + 1
        g = [[] for i in range(n)]
        for a, b in e:
            g[a].append(b)
            g[b].append(a)
        s = [0] * n
        t = [0] * n
        q = [-1] * n
        def dfs(x, fa):
            for y in g[x]:
                if y != fa:
                    v = 2 - y % 2
                    dfs(y, x)
                    if s[y] + v > s[x]:
                        t[x] = s[x]
                        s[x] = s[y] + v
                        q[x] = y
                    elif s[y] + v > t[x]:
                        t[x] = s[y] + v
        dfs(0, -1)
        f = [0] * n
        f[0] = s[0]
        def reroot(x, fa, w):
            w += 2 - fa % 2
            if w > s[x]:
                t[x] = s[x]
                s[x] = w
                q[x] = fa
            elif w > t[x]:
                t[x] = w
            for y in g[x]:
                if y != fa:
                    reroot(y, x, t[x] if y == q[x] else s[x])
        for i in g[0]:
            reroot(i, 0, t[0] if i == q[0] else s[0])
        return s
