class Solution {
public:

    pair<int, int> dfs (int src, int pa, vector<vector<int>> &graph, vector<pair<int, int>> &child) {
        int odd = 0, even = 0;
        int msf = 0;
        for (auto &x: graph[src]) {
            if (x == pa) continue;
            auto yy = dfs(x, src, graph, child);
            int tmp = 2*yy.second + yy.first;
            if (tmp > msf) {
                msf = tmp;
                odd = yy.first;
                even = yy.second;
            }
        }
        if (src & 1) odd++;
        else even++;
        return (child[src] = {odd, even});
    }

    void dfs2 (int src, int pa, vector<int> &res, vector<vector<int>> &graph, vector<pair<int, int>>&child, int cf) {
        int msf1 = cf;
        int msf2 = 0;
        for (auto &x: graph[src]) {
            if (x == pa) continue;
            int msf = 2*child[x].second + child[x].first;
            if (msf >= msf1) {
                msf2 = msf1;
                msf1 = msf;
            } else if (msf > msf2) {
                msf2 = msf;
            }
        }
        res[src] = msf1;
        int ext = 1;
        if (src % 2 == 0) ext++;
        for (auto &x: graph[src]) {
            if (x == pa) continue;
            int msf = 2*child[x].second + child[x].first;
            if (msf1 != msf) {
                dfs2(x, src, res, graph, child, msf1+ext);
            } else {
                dfs2(x, src, res, graph, child, msf2+ext);
            }
        }
    }

    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size() + 1;
        vector<vector<int>> graph(n);
        for (auto &x: edges) {
            graph[x[0]].push_back(x[1]);
            graph[x[1]].push_back(x[0]);
        }
        vector<pair<int, int>> child(n);

        dfs(0, -1, graph, child);

        vector<int> res(n);
        int cf = 0;
        dfs2(0, -1, res, graph, child, cf);
        return res;

    }
};
