class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {

        int n = edges.size()+1;
        vector<int> res( n, 0);

        vector< vector<int> > adj( n, vector<int>());

        vector<int> te( n, 0);
        vector<int> to( n, 0);
        vector< multiset<int> > dp( n, multiset<int>());

        for( auto & edge: edges) {
            int u = edge[0];
            int v = edge[1];
            adj[u].push_back(v);
            adj[v].push_back(u);
        }


        function<void(int,int,int,int)> dfs1;
        function<void(int,int,int)> dfs2;

        dfs1 = [&]( int id, int par, int co, int ce)->void {  
            co += (id%2==1);
            ce += (id%2==0);
            to[id] = co;
            te[id] = ce;
            dp[id].insert(0);
            for( int nxt: adj[id]) {
                if(nxt==par) {
                    continue;
                }
                dfs1( nxt, id, co, ce);
                int temp = (*dp[nxt].rbegin())+1+(nxt%2==0);
                dp[id].insert(temp);
            }
            res[id] = (*dp[id].rbegin());
        };

        dfs2 = [&]( int id, int par, int maxVal)->void {
            int vej = te[id] - (id%2==0);
            int voj = to[id] - (id%2==1);
            int kj = 2*vej + voj;
            if(par!=-1) {
                res[id] = max( res[id], maxVal+kj);
            }
            for( int nxt: adj[id]) {
                if(nxt==par) {
                    continue;
                }
                int temp = (*dp[nxt].rbegin())+1+(nxt%2==0);
                dp[id].erase(dp[id].find(temp));
                int currVal = (*dp[id].rbegin()) - kj;
                dfs2( nxt, id, max( maxVal, currVal));
                dp[id].insert(temp);
            }
        };



        dfs1( 0, -1, 0, 0);
        dfs2( 0, -1, INT_MIN);

        return res;
    }
};
