import numpy as np
class TreeNode:
    def __init__(self, i):
        self.id = i
        self.neighbors = {}
        self.largest = [-1,-np.inf]
        self.second = [-1, -np.inf]

class Solution:
    def timeTaken(self, edges: List[List[int]]) -> List[int]:
        n = len(edges)+1
        nodes = [TreeNode(i) for i in range(n)]
        for u,v in edges:
            nodes[u].neighbors[v] = np.nan
            nodes[v].neighbors[u] = np.nan
        self.rootedTree(nodes, 0, -1)
        self.rootedTree2(nodes,0,-1)
        return [node.largest[1] for node in nodes]

    def rootedTree(self, nodes, root, parent):
        # rooted at node 0
        leaf = True
        lid = -1
        l = 0
        sid = -1
        s = 0
        for v in nodes[root].neighbors:
            if v != parent:
                leaf = False
                self.rootedTree(nodes, v, root)
                d = (v+1) % 2 + 1
                if nodes[v].largest[1] + d >= l:
                    s = l
                    sid = lid
                    l = nodes[v].largest[1] + d
                    lid = v
                elif nodes[v].largest[1] + d >= s:
                    s = nodes[v].largest[1] + d
                    sid = v
        nodes[root].largest = [lid,l]
        nodes[root].second = [sid,s]

    def rootedTree2(self, nodes, root, parent):
        if parent >= 0:
            d = (parent + 1) % 2 + 1
            if nodes[parent].largest[0] == root:
                l = nodes[parent].second[1] + d
            else:
                l = nodes[parent].largest[1] + d
            if l > nodes[root].largest[1]:
                nodes[root].second = nodes[root].largest
                nodes[root].largest = [parent, l]
            elif l > nodes[root].second[1]:
                nodes[root].second = [parent, l]
        for v in nodes[root].neighbors:
            if v != parent:
                self.rootedTree2(nodes, v, root)
