class Solution {
    int dfs(int id,int fa,vector<vector<int>>&adj,vector<int>&mx,vector<int>&sec){
        int mx_tmp = 0;
        int sec_tmp = 0;
        for(auto next : adj[id]){
            if(next == fa)  continue;
            int val = dfs(next,id,adj,mx,sec)+1;
            if(next%2 == 0){
                val++;
            }
            if(val > mx_tmp){
                sec_tmp = mx_tmp;
                mx_tmp = val;
            }else if(val > sec_tmp){
                sec_tmp = val;
            }
        }
        mx[id] = mx_tmp;
        sec[id] = sec_tmp;
        return mx_tmp;
    }
    void func(int id,int fa,vector<vector<int>>&adj,vector<int>&mx,vector<int>&sec){
        if(id != 0){
            int add = 1;
            if(id %2 == 0)  add++;
            int fadd = 1;
            if(fa %2 == 0)  fadd++;
            if(mx[id]+add < mx[fa]){
                int val = mx[fa]+fadd;
                sec[id] = mx[id];
                mx[id] = val;
            }else{
                int val = sec[fa]+fadd;
                if(val > mx[id]){
                    sec[id] = mx[id];
                    mx[id] = val;
                }else if(val > sec[id]){
                    sec[id] = val;
                }
            }
        }
        for(auto obj : adj[id]){
            if(obj == fa)   continue;
            func(obj,id,adj,mx,sec);
        }
    }
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size();
        n++;
        vector<int>v;
        vector<vector<int>>adj(n,v);
        for(auto obj : edges){
            int a = obj[0],b = obj[1];
            adj[a].emplace_back(b);
            adj[b].emplace_back(a);
        }
        vector<int>mx(n,0);
        vector<int>sec(n,0);
        dfs(0,-1,adj,mx,sec);
        func(0,-1,adj,mx,sec);
        return mx;
    }
};
