class Solution:
    def timeTaken(self, edges: List[List[int]]) -> List[int]:
        nodeMap = defaultdict(list)
        for u,v in edges:
            nodeMap[u].append(v)
            nodeMap[v].append(u)
        subtree = [0]*(len(edges)+1)
        def dfs1(node, parent):
            add = 2
            if node%2==1:
                add = 1
            res = 0
            for neighbor in nodeMap[node]:
                if neighbor!=parent:
                    res = max(res, dfs1(neighbor,node))
            subtree[node] = res
            return res+add
        res = [0]*(len(edges)+1)
        dfs1(0,0)
        res[0]=subtree[0]
        #print(subtree[3])
        def dfs2(node, parent,mx):
            add = 2
            if parent%2==1:
                add = 1
            #print(node,mx,add)
            mx1 = 0
            mx1i = -1
            mx2 = 0
            msub = 0
            for neighbor in nodeMap[node]:
                if neighbor!=parent:
                    a = 2
                    if neighbor%2==1:
                        a = 1
                    msub = max(subtree[neighbor],msub)
                    val = subtree[neighbor]+a
                    if val>=mx1:
                        mx2=mx1
                        mx1=val
                        mx1i=neighbor
                    elif val>mx2:
                        mx2=val
            if node!=0:
                res[node] = max(mx1,mx+add)
            if node!=0:
                #print(node,mx1,mx1i,mx2,msub)
                if mx+add>=mx1:
                    #print(node,mx+add)
                    mx2=mx1
                    mx1=mx+add
                    mx1i=-1
                elif mx+add>mx2:
                    mx2=mx+add
            #print(node,mx1,mx1i,mx2,msub)
            for neighbor in nodeMap[node]:
                if neighbor!=parent:
                    if neighbor==mx1i:
                        dfs2(neighbor,node,mx2)
                    else:
                        dfs2(neighbor,node,mx1)
        dfs2(0,0,0)
        return res
