const int maxn = 1e5+10;
vector<int> v[maxn];
int dp[maxn];
int ans[maxn];
void dfs(int x,int f) 
{

	for(int u : v[x]) 
	{
		if(u == f) continue;
		dfs(u,x);
		if(u % 2 != 0) dp[x] = max(dp[x],dp[u] + 1);
		else dp[x] = max(dp[x],dp[u] + 2);
	}
}
void dfs2(int x,int f,int deep) 
{
	if(x)  
	{
		ans[x] = max(dp[x], deep);
	}
	int id1 = 0,id2 = 0,mx1 = 0,mx2 = 0;
	id2 = f,mx2 = deep;
	for(int u : v[x])  
	{
		if(u == f) continue;
		if(dp[u] + (u % 2 == 0 ? 2 : 1) > mx1) 
		{
			id1 = u;
			mx1 = dp[u] + (u % 2 == 0 ? 2 : 1);
		}
		if(mx1 > mx2) 
		{
			swap(mx1,mx2);
			swap(id1,id2);
		}
	}
	int z = 1;
	if(x % 2 == 0) z = 2;
	for(int &u : v[x]) 
	{
		if(u == f) continue;
		int now = mx2;
		if(id2 == u) now = mx1;
		dfs2(u,x,now+z);
	}
}
class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
    	int n = edges.size() + 1;
        for(int i = 0; i <= n; ++i) 
        {
            v[i].clear();
            dp[i] = ans[i] = 0;
        }
        for(vector<int> a : edges) 
        {
        	v[a[0]].push_back(a[1]);
        	v[a[1]].push_back(a[0]);
        }
        dfs(0,-1);
        ans[0] = dp[0];
        dfs2(0,-1,0);
        vector<int> res(n);
        for(int i = 0; i < n; ++i) res[i] = ans[i];
        return res;

    }
};
