class Solution {
public:
int N = 1e5 + 1;
vector<int>g[100000];
vector<int>time;
vector<pair<int,int>>dp;
void updata(int num,int id){
    if(num > dp[id].first){
        dp[id].second = dp[id].first;
            dp[id].first = num;
        }else if(num > dp[id].second){
        dp[id].second = num;
    }
}
    void dfs1(int u,int fa){
        dp[u] = {0,0};
       for(int v : g[u]){
          if(v == fa)continue;
          dfs1(v,u);
          int c = v % 2 ? 1 : 2;
          updata(c + dp[v].first,u);

       }
    }
    void dfs2(int u,int fa){
        time[u] = dp[u].first;

        for(int v : g[u]){
            if(v == fa)continue;
            auto temp = dp[v];
            int c = dp[u].second + (u % 2 ? 1 : 2);
            if(dp[u].first != temp.first + (v % 2 ? 1 : 2)){
                c = dp[u].first + (u % 2 ? 1 : 2);
            }
            updata(c,v);
            dfs2(v,u);
            dp[v] = temp;
        }

    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size() + 1;
        time.resize(n);
        dp.resize(n);
        for(auto e : edges){
            g[e[0]].push_back(e[1]);
            g[e[1]].push_back(e[0]);
        }
        dfs1(0,-1);
        dfs2(0,-1);
        //for(int i = 0; i< n;i++)cout << dp[i].first << ' ' << dp[i].second << endl;
        return time;
    }
};
