void dfs0(int x, int p = -1) {
	if (p == -1) {
		e[x].pb(-1);
		e[x].insert(e[x].begin(), -1);
	} else {
		fi(0, sz(e[x]) - 1) {
			if (e[x][i] == p) {
				swap(e[x][i], e[x][0]);
			}
		}
		e[x].pb(p);
	}

	d[x] = 0;
	for (int y : e[x]) {
		if (y == p) continue;
		dfs0(y, x);
		d[x] = max(d[x], getd(y) + d[y]);
	}
}

void dfs1(int x, int hv, int p = -1) {
