		int nhv = max(vpref[i - 1], vsuf[i + 1]);
		nhv = max(nhv, hv + getd(p));
		dfs1(y, nhv, x);
	}
	//err("x = %d hv = %d p = %d f = %d d = %d\n", x, hv, p, f[x], d[x]);
}

