class Solution {
public:
    int n;
    vector<int>v[200010],ans;
    int id[200010][2], f[200010][2];
    void dfs(int x,int fa){
        for(int i=0;i<v[x].size();i++){
            int y=v[x][i];
            if(y==fa)continue;
            dfs(y,x);
            if(f[y][0]+(y&1?1:2)>f[x][0]){
                f[x][1]=f[x][0];
                id[x][1]=id[x][0];
                f[x][0]=f[y][0]+(y&1?1:2);
                id[x][0]=y;
            }
            else if(f[y][0]+(y&1?1:2)>f[x][1]){
                f[x][1]=f[y][0]+(y&1?1:2);
                id[x][1]=y;                
            }
        }
    }
    void dfs1(int x,int fa){
        ans[x]=f[x][0];
        for(int i=0;i<v[x].size();i++){
            int y=v[x][i];
            if(y==fa)continue;
            if(id[x][0]==y){
                if(f[x][1]+(x&1?1:2)>f[y][0]){
                    f[y][1]=f[y][0];
                    id[y][1]=id[y][0];
                    f[y][0]=f[x][1]+(x&1?1:2);
                    id[y][0]=x;
                }
                else if(f[x][1]+(x&1?1:2)>f[y][1]){
                    f[y][1]=f[x][1]+(x&1?1:2);
                    id[y][1]=x;                    
                }
            }
            else{
                if(f[x][0]+(x&1?1:2)>f[y][0]){
                    f[y][1]=f[y][0];
                    id[y][1]=id[y][0];
                    f[y][0]=f[x][0]+(x&1?1:2);
                    id[y][0]=x;
                }
                else if(f[x][0]+(x&1?1:2)>f[y][1]){
                    f[y][1]=f[x][0]+(x&1?1:2);
                    id[y][1]=x;                    
                }                
            }
            dfs1(y,x);
        }        
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        n=edges.size()+1;
        ans.resize(n);
        for(int i=0;i<n-1;i++){
            int x=edges[i][0],y=edges[i][1];
            v[x].push_back(y);
            v[y].push_back(x);
        }
        dfs(1,-1);
        dfs1(1,-1);
        return ans;
    }
};
