class Solution {
public:
  vector<int> timeTaken(vector<vector<int>>& g) {
    int n = g.size()+1;
    vector<vector<int>> co(n);
    vector<int> out(n);
    for(vector<int>&e:g) co[e[0]].push_back(e[1]), co[e[1]].push_back(e[0]);
    function <void(int, int)> dfs = [&](int x, int f) {
      for(int s:co[x]) if(s!=f){
        // out[x] = 2-(s%2);
        // ans[0] = max(ans[0], out[x]);
        // in[x] = 2-(x%2);
        dfs(s, x);
        out[x] = max(out[x], out[s]+ 2-s%2);
      }
      // printf("out[%d] %d\n", x, ans[x]);
    };
    dfs(0, 0);
    function <void(int, int, int)> dfs2 = [&](int x, int f, int v) {
      // printf("v[%d] %d\n", x, v);
      out[x] = max(out[x], v);
      int to_x = 2- x%2;
      int mxi = -1, mx1 = v, mx2=0;
      for(int ss: co[x]) if(ss!=f) {
        int cur = 2-ss%2+out[ss];
        if(cur > mx1) {
          mxi = ss;
          mx2 = mx1;
          mx1 = cur;
        }
        else if(cur>mx2){
          mx2 = cur;
        }
      }
      for(int s:co[x]) if(s!=f){
        int mx = s==mxi? mx2:mx1;
        dfs2(s, x, mx + to_x);
      }
    };
    dfs2(0, -1, 0);
    return out;
  }
};
