class Solution {
public:
    static const int maxn = 2e5+10;
    vector<array<int,2>>edge[maxn];
    int mx[maxn][2];
    vector<int>res;
    int dfs(int u,int fa){
        for(auto w:edge[u]){
            int v = w[0];
            if(w[0] == fa)continue;
            int tmp = dfs(w[0],u) + (v % 2 ? 1 : 2);
            mx[u][1] = max(mx[u][1],tmp);
            if(mx[u][1] > mx[u][0])swap(mx[u][1],mx[u][0]);
        }
        return mx[u][0];
    }
    void solve(int u,int fa,int maxLen){
        res[u] = max(mx[u][0],maxLen);
        for(auto w:edge[u]){
            if(w[0] == fa)continue;
            int tmp;
            int v = w[0];
            if(mx[u][0] == mx[v][0] + (v%2?1:2))
                tmp = max(maxLen,mx[u][1]) + (u % 2?1:2);
            else tmp = max(maxLen,mx[u][0]) + (u % 2?1:2);
            solve(w[0],u,tmp);
        }
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = 0;
        for(auto e:edges){
            edge[e[0]].push_back({e[1],e[1]%2?1:2});
            edge[e[1]].push_back({e[0],e[0]%2?1:2});
            n = max(n,max(e[0],e[1]));
        }
        dfs(0,-1);
        res = vector<int>(n + 1);
        solve(0,-1,0);
        return res;
    }
};
