class Solution {
public:

    void dfs(int i,int par,vector<int>g[],vector<int>&dp){
        dp[i]=0;
        for(auto it:g[i]){
            if(it==par) continue;
             dfs(it,i,g,dp);
            //else dfs(it,i,g,dp,t+2);
            if(it%2) dp[i]=max(dp[i],dp[it]+1);
            else dp[i]=max(dp[i],dp[it]+2);

        }
    }

    void dfs1(int i,int par,vector<int>&dp,int part,vector<int> &ans,vector<int> g[]){
        int mx=0;
        int secmx=0;
        int fmx=0;
        int smx=0;
        for(auto it:g[i]){
            if(it==par) continue;
            if(dp[it]+1+(it%2==0)>mx){
                secmx=mx;
                smx=fmx;
                mx=dp[it]+1+!(it%2);
                fmx=it;
            }
            else if(dp[it]+1+!(it%2)>secmx){
                secmx=dp[it]+1+!(it%2);
                smx=it;
            }
        }
        if(par!=-1)
        ans[i]=max(part,mx);
        else ans[i]=mx;
        for(auto it:g[i]){
            if(it==par) continue;
            if(it==fmx){
                dfs1(it,i,dp,max(secmx,part)+1+!(i%2),ans,g);
            }
            else dfs1(it,i,dp,max(mx,part)+1+!(i%2),ans,g);
        }
    }

    vector<int> timeTaken(vector<vector<int>>& edges) {
     int n=edges.size()+1;
     vector<int> g[n];
     for (auto it:edges){
        g[it[0]].push_back(it[1]);
        g[it[1]].push_back(it[0]);
     }
     vector<int> dp(n,0);
     dfs(0,-1,g,dp);
     vector<int> ans(n,0);
     dfs1(0,-1,dp,0,ans,g);
    return ans;
    }
};
