const int inf = 1e9+7;
class Solution {
public:
    vector<int>adj[100005];
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int m = edges.size(), n = m+1;
        for(auto e: edges){
            adj[e[0]].push_back(e[1]);
            adj[e[1]].push_back(e[0]);
        }
        vector<int>tim(n, inf), max_time(n, -inf);
        auto dfs = [&](int v, int fa, int t, auto &&dfs) -> void{
            tim[v] = t;
            for(auto u: adj[v]){
                if(u == fa)continue;
                if(u&1){
                    dfs(u, v, t+1, dfs);
                } else{
                    dfs(u, v, t+2, dfs);
                }
            }
            max_time[v] = tim[v];
            for(auto u: adj[v]){
                if(u == fa)continue;
                max_time[v] = max(max_time[v], max_time[u]);
            }
        };
        dfs(0, -1, 0, dfs);
        // for(auto r: tim)cout<<r<<" ";cout<<'\n';
        // for(auto r: max_time)cout<<r<<" ";cout<<'\n';
        vector<int>res(n, inf);
        auto help_me = [&](int v, int fa, int t1, int t2, auto &&help_me) -> void{
            vector<int>cur_time{-inf, -inf};
            for(auto u: adj[v]){
                if(u == fa)continue;
                int time = max_time[u] - tim[u] + 1;
                if((u % 2) == 0){
                    time++;
                } 
                cur_time.push_back(time);
            }
            if(fa != -1){
                int cost_v = !(v&1) + 1;
                int cost_f = !(fa&1)+ 1;
                if(res[fa] == (max_time[v] - tim[v] + cost_v)){
                    cur_time.push_back(t2 + cost_f);
                    cur_time.push_back(cost_f);
                } else{
                    cur_time.push_back(t1 + cost_f);
                }
            }
            sort(cur_time.begin(), cur_time.end(), greater<int>());
            res[v] = cur_time[0];
            for(auto u: adj[v]){
                if(u == fa)continue;
                help_me(u, v, cur_time[0], cur_time[1], help_me);
            }
        };
        help_me(0, -1, -inf, -inf, help_me);
        return res;
    }
};
