class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& G) {
        int n = G.size() + 1;
        vector<int> edge[n];
        for (auto sb:G){
            int x = sb[0], y = sb[1];
            edge[x].push_back(y);
            edge[y].push_back(x);
        }
        vector<int> f(n), dp(n);
        function<void(int, int)> dfs1 = [&](int x, int fa){
            for (auto y:edge[x]){
                if (y==fa) continue;
                dfs1(y, x);
                int tmp = f[y] + (y%2==0) + 1;
                dp[x] =max(dp[x], tmp);
                if (dp[x]>f[x]) swap(dp[x], f[x]);
            }
        };
        vector<int> ans(n);
        function<void(int, int)> dfs2 = [&](int x, int fa){
            ans[x] = f[x];
            for (auto y:edge[x]){
                if (y==fa) continue;
                int val = f[x];
                if (val == f[y] + (y%2==0) + 1) val = dp[x];
                val+=(x%2==0) + 1;
                dp[y] = max(dp[y], val);
                if (dp[y]>f[y]) swap(dp[y], f[y]);
                dfs2(y, x);
            }
        };
        dfs1(0, -1), dfs2(0, -1);
        return ans;
    }
};
