class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n=edges.size()+1;
        vector<vector<int>>e(n);
        for(auto&x:edges){
            int u=x[0],v=x[1];
            e[u].push_back(v);
            e[v].push_back(u);
        }
        vector<int>times(n);
        function<void(int x,int fa)>dfs=[&](int x,int fa){
            for(auto y:e[x]){
                if(y==fa)continue;
                dfs(y,x);
                times[x]=max(times[x],times[y]+(y%2?1:2));
            }
        };
        dfs(0,-1);
        vector<int>ans(n);
        function<void(int x,int fa,int mx)>work=[&](int x,int fa,int mx){
            ans[x]=max(mx,times[x]);
            vector<int>tmx(2,mx+(x%2?1:2));
            for(auto y:e[x]){
                if(y==fa)continue;
                int cost=times[y]+(y%2?1:2)+(x%2?1:2);
                for(int j=0;j<2;j++){
                    if(tmx[j]<cost)swap(tmx[j],cost);
                }
            }
            for(auto y:e[x]){
                if(y==fa)continue;
                int cost=times[y]+(y%2?1:2)+(x%2?1:2);
                int v=(cost==tmx[0]?tmx[1]:tmx[0]);
                work(y,x,v);
            }
        };
        work(0,-1,0);
        return ans;
    }
};
