class Solution {
public:
    vector<int>ans;
    vector<int>subT;
    vector<vector<pair<int,int>>> g;

    void rec(int i, int par)
    {
        for(auto& pii:g[i])
        {
            int it = pii.first;
            if(it!=par)
            {
                rec(it,i);
                if(it%2)
                {
                    subT[i] = max(subT[it] + 1, subT[i]);
                    pii.second = subT[it]+1;
                }
                else
                {
                    subT[i] = max(subT[it] + 2, subT[i]);
                    pii.second = subT[it]+2;
                }
            }
        }
    }

    void rec2(int i, int par, int parAns)
    {
        // cout<<i<<" "<<par<<" "<<parAns<<endl;
        multiset<int>s;
        s.insert(0);
        for(auto pii:g[i])
        {
            if(pii.first!=par)
            s.insert(pii.second);
        }

        for(auto pii:g[i])
        {
            int it = pii.first;
            if(it!=par)
            {
                s.erase(s.find(pii.second));
                // cout<<it<<" "<<*s.rbegin()<<endl;
                // if(it%2)
                // rec2(it,i,parAns + 1 + *s.rbegin());
                // else
                // rec2(it,i,parAns + 2 + *s.rbegin());
                if(i%2)
                {
                    rec2(it,i,max(parAns+1,1+*s.rbegin()));
                }   
                else
                {
                    rec2(it,i,max(parAns+2,2+ *s.rbegin()));
                }             
                s.insert(pii.second);
            }
        }
        ans[i] = max(parAns, subT[i]);
        // if(par==-1)
        // ans[i]=subT[i];
        // else if(par%2)
        // ans[i] = max(parAns + 1,subT[i]);
        // else
        // ans[i] = max(parAns + 2,subT[i]);
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size() + 1;
        ans.assign(n, 0);
        subT.assign(n, 0);
        g.assign(n+1,{});
        for(auto i:edges)
        {
            g[i[0]].push_back({i[1],0});
            g[i[1]].push_back({i[0],0});
        }   

        rec(0,-1);
        rec2(0,-1,0);
        return ans;
    }
};
