class Solution {
public:

    void dfs(int v, int p, vector<int> &first, vector<int> &second, vector<int> &c, vector<vector<int>> &adj){

        for (auto x : adj[v]) {
            if (x == p) continue;
            dfs(x, v,first,second,c,adj);
            if(x%2==1){
                if (first[x] + 1 > first[v]) {
                    second[v] = first[v];
                    first[v] = first[x] + 1;
                    c[v] = x;
                } else if (first[x] + 1 > second[v]) {
                    second[v] = first[x] + 1;
                }
            }
            else{
                if (first[x] + 2 > first[v]) {
                    second[v] = first[v];
                    first[v] = first[x] + 2;
                    c[v] = x;
                } else if (first[x] + 2 > second[v]) {
                    second[v] = first[x] + 2;
                }
            }
        }

    }

    void dfs2(int v, int p, vector<int> &first, vector<int> &second, vector<int> &c, vector<vector<int>> &adj){

        for (auto x : adj[v]) {
            if (x == p) continue;
            if(v%2==1){
                if (c[v] == x) {
                    if (first[x] < second[v] + 1) {
                        second[x] = first[x];
                        first[x] = second[v] + 1;
                        c[x] = v;
                    } else {
                        second[x] = max(second[x], second[v] + 1);
                    }
                } else {
                    second[x] = first[x];
                    first[x] = first[v] + 1;
                    c[x] = v;
                }
            }
            else{
                if (c[v] == x) {
                    if (first[x] < second[v] + 2) {
                        second[x] = first[x];
                        first[x] = second[v] + 2;
                        c[x] = v;
                    } else {
                        second[x] = max(second[x], second[v] + 2);
                    }
                } else {
                    second[x] = first[x];
                    first[x] = first[v] + 2;
                    c[x] = v;
                }
            }
            dfs2(x, v,first,second,c,adj);
        }

    }

    vector<int> timeTaken(vector<vector<int>>& edges) {

        int n=edges.size();
        n++;

        vector<vector<int>> adj(n);
        for(int i=0;i<n-1;i++){
            adj[edges[i][0]].push_back(edges[i][1]);
            adj[edges[i][1]].push_back(edges[i][0]);
        }

        vector<int> first(n),second(n), c(n);

        dfs(0,-1,first,second,c,adj);
        dfs2(0,-1,first,second,c,adj);

        return first;
    }

};
