class Solution {
public:

    int buildTree(vector<int>V[],vector<pair<int,int>>&dp1,vector<pair<int,int>>&dp2,int s,int p){
        int a = 0,b = 0;
        for(auto i : V[s]){
            if(i == p)continue;
            int p = buildTree(V,dp1,dp2,i,s);
            if(i % 2)p++;
            else p += 2;
            if(p >= a){
                b = a;a = p;
                dp2[s].second = dp1[s].first;
                dp2[s].first = i;
            }
            else if(p > b){
                dp2[s].second = i;
                b = p;
            }
        }
        dp1[s] = {a,b};
        return max(a,b);
    }

    void findValues(vector<int>V[],vector<pair<int,int>>&dp1,vector<pair<int,int>>&dp2,int s,int p){
        if(s != 0){
            int k = 0;
            if(dp2[p].first == s)k = dp1[p].second + ((p % 2) ? 1 : 2);
            else k = dp1[p].first + ((p % 2) ? 1 : 2);
            // cout<<s<<" "<<k<<endl;
            if(k >= dp1[s].first){
                dp1[s].second = dp1[s].first;
                dp2[s].second = dp2[s].first;
                dp1[s].first = k;
                dp2[s].first = p;
            }else if(k > dp1[s].second){
                dp1[s].second = k;
                dp2[s].second = p;
            }
        }
        for(auto i : V[s]){
            if(i != p)findValues(V,dp1,dp2,i,s);
        }
        return;
    }

    vector<int> timeTaken(vector<vector<int>>& e) {
        int n = e.size() + 1;
        vector<int>V[n];
        for(auto v : e){
            V[v[0]].push_back(v[1]);
            V[v[1]].push_back(v[0]);
        }
        vector<pair<int,int>>dp1(n,{0,0}),dp2(n,{-1,-1});
        int k = buildTree(V,dp1,dp2,0,-1);
        // cout<<k<<endl;
        findValues(V,dp1,dp2,0,-1);
        vector<int>res(n,0);
        for(int i = 0;i < n;i++){
            res[i] = dp1[i].first;
        }
        return res;
    }
};
