class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n=edges.size()+1;
        vector<vector<int>>next(n);
        for(auto &e:edges){
            next[e[0]].push_back(e[1]);
            next[e[1]].push_back(e[0]);
        }

        auto get=[&](int u)->int{
            return ((u&1)?1:2);
        };

        vector<int>res(n),f(n),s(n);
        function<int(int,int)>get_info=[&](int u,int pre){
            f[u]=s[u]=0;
            for(auto &v:next[u]){
                if(v!=pre){
                    int d=get_info(v,u)+get(v);
                    if(d>=f[u]){
                        s[u]=f[u];
                        f[u]=d;
                    }
                    else if(d>s[u]){
                        s[u]=d;
                    }
                }
            }
            return f[u];
        };
        get_info(0,-1);
        function<void(int,int,int)>get_res=[&](int u,int pre,int pred){
            res[u]=max(pred,f[u]);
            for(auto &v:next[u]){
                if(v!=pre){
                    if(f[u]==(f[v]+get(v))){
                        get_res(v,u,max(pred,s[u])+get(u));
                    }
                    else{
                        get_res(v,u,max(pred,f[u])+get(u));
                    }
                }
            }
        };
        get_res(0,-1,0);
        return res;
    }
};
