class Solution {

public:
typedef long long ll;
vector<ll> e[100005];
ll n,h[200005],a[100005];
void dfs(ll x){
    //ll s=1+(x%2==0);
    for (auto y:e[x]){
        if (!h[y]){
            h[y]=1;
            dfs(y);
            a[x]=max(a[x],a[y]+1+(y%2==0));
        }

    }
    //a[x]+=s;
}
struct zu{
    ll id;
    ll zhi;
    bool operator<(const zu aaa) const{
        return zhi>aaa.zhi;
    }
};
ll h1[100005],b[200005];
void dfs1(ll x,ll y){
    b[x]=max(a[x],y);
    vector<zu> e1;
    for (auto u:e[x]){
        if (!h1[u]) e1.push_back({u,a[u]+1+(u%2==0)+1+(x%2==0)});
    }
    e1.push_back({x,y+1+(x%2==0)});
    sort(e1.begin(),e1.end());
    for (auto u:e[x]){
        if (!h1[u]){
            h1[u]=1;
            ll xx=e1[0].zhi;
            if (e1[0].id==u) {
                xx=e1[1].zhi;
            }

            dfs1(u,xx);
        }
    }
}
    vector<int> timeTaken(vector<vector<int>>& edges) {
        n=edges.size()+1;
        for (auto x:edges){
            e[x[0]].push_back(x[1]);
            e[x[1]].push_back(x[0]);
        }
        h[1]=h1[1]=1;
        dfs(1);
        dfs1(1,0);
        vector<int> e2;
        for (int i=0;i<n;i++) e2.push_back(b[i]);
        return e2;
    }
};
