class Solution {
public:
    vector<int> dist(vector<int> *adj, int &n, int st){
        queue<int> temp;
        vector<int> dis(n, -1);
        temp.push(st);
        dis[st] = 0;
        while(!temp.empty()){
            int curr = temp.front();
            temp.pop();
            for(auto &elem : adj[curr]){
                if(dis[elem] == -1){
                    if(elem%2 == 0) dis[elem] = dis[curr] + 2;
                    else dis[elem] = dis[curr] + 1;
                    temp.push(elem);
                }
            }
        }
        return dis;
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size() + 1;
        vector<int> adj[n];
        for(auto &elem : edges){
            adj[elem[0]].push_back(elem[1]);
            adj[elem[1]].push_back(elem[0]);
        }
        vector<int> d = dist(adj, n, 0);
        int end1 = 0;
        for(int i = 0; i < d.size(); i++ ){
            if(d[end1] < d[i]) end1 = i;
        }
        vector<int> d1 = dist(adj, n, end1);
        int end2 = 0;
        for(int i = 0; i < d1.size(); i++ ){
            if(d1[end2] < d1[i]) end2 = i;
        }
        vector<int> d2 = dist(adj, n, end2);
        // cout<<end1<<" "<<end2<<"\n";
        // for(auto &elem : d1){
        //     cout<<elem<<" ";
        // }
        // cout<<"\n";
        // for(auto &elem : d2){
        //     cout<<elem<<" ";
        // }
        // cout<<"\n";
        vector<int> ans;
        for(int i = 0; i < n ; i++){
            int chk = 0;
            if(i%2 == end1%2){
                chk = max(chk, d1[i]);
            }else{
                if(i%2 == 0 and end1%2 != 0)
                chk = max(chk, d1[i] - 1);
                else 
                chk = max(chk, d1[i] + 1);
            }

            if(i%2 == end2%2){
                chk = max(chk, d2[i]);
            }else{
                if(i%2 == 0 and end2%2 != 0)
                chk = max(chk, d2[i] - 1);
                else 
                chk = max(chk, d2[i] + 1);
            }

            ans.push_back(chk);
        }
        return ans;



    }
};
