class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& e) {
        int n = e.size() + 1;
        vector<int> da(n);
        vector<vector<int>> v(n);
        vector<int> ans(n);
        for (auto& i : e) {
            v[i[0]].push_back(i[1]);
            v[i[1]].push_back(i[0]);
        }
        function<void(int, int)> f1 = [&](int a, int b) {
            for (int i : v[a])
                if (i != b) {
                    f1(i, a);
                    da[a] = max(da[a], da[i] + (i % 2 == 1 ? 1 : 2));
                }
        };
        f1(0, -1);
        function<void(int, int, int)> f2 = [&](int a, int b, int c) {
            // printf("a=%d b=%d da=%d c=%d\n", a, da[a], c);
            int d = b == -1 ? 0 : (c + (b % 2 == 1 ? 1 : 2));
            ans[a] = max(da[a], d);
            // printf("ans[%d]=%d\n", a, ans[a]);
            vector<int> va(v[a].size() + 1), vb(v[a].size() + 1);
            for (int i = 0; i < v[a].size(); i++) {
                va[i + 1] = va[i];
                if (v[a][i] != b) {
                    va[i + 1] = max(va[i], da[v[a][i]] + (v[a][i] % 2 == 1 ? 1 : 2));
                } else {
                    va[i + 1] = va[i];
                }
            }
            for (int i = v[a].size() - 1; i >= 0; i--) {
                vb[i] = vb[i + 1];
                if (v[a][i] != b) {
                    vb[i] = max(vb[i + 1], da[v[a][i]] + (v[a][i] % 2 == 1 ? 1 : 2));
                } else {
                    vb[i] = vb[i + 1];
                }
            }
            for (int i = 0; i < v[a].size(); i++)
                if (v[a][i] != b)
                    f2(v[a][i], a, max(d, max(va[i], vb[i + 1])));
        };
        f2(0, -1, 0);
        return ans;
    }
};
