class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size() + 1;
        vector<vector<int>> adj(n);
        for(auto it:edges){
            adj[it[0]].push_back(it[1]);
            adj[it[1]].push_back(it[0]);
        }

        vector<int> mxw(n , 0);
        vector<int> dp(n , 0);
        vector<int> fin(n , 0);
        vector<int> p(n , 0);

        auto dfs = [&](auto self , int u , int p = -1)->void{
            multiset<int> s;
            for(auto it:adj[u]){
                if(it == p)continue;
                self(self  , it , u);
                s.insert(dp[it] + (it%2?1:2));
                dp[u] = max(dp[u] , dp[it] + (it%2?1:2));
            }

            for(auto it:adj[u]){
                if(it == p)continue;
                s.extract(dp[it] + (it%2?1:2));
                if(s.size())mxw[it] = *s.rbegin();
                s.insert(dp[it] + (it%2?1:2));
            }

        };
        dfs(dfs , 0 , -1);
        fin[0] = dp[0];
        auto dfs2 = [&](auto self , int u , int p , int val)->void{
             fin[u] = max({dp[u] , mxw[u] + (p%2?1:2) , val});
             for(auto it:adj[u]){
                 if(it == p)continue;
                 self(self , it , u , max(val , mxw[u] + (p%2?1:2))  + (u%2?1:2));
             }
        };
        for(auto i:adj[0])dfs2(dfs2 , i , 0 , mxw[i] + 2);
        return fin;
    }
};
