class Solution {
public:

    vector<int> dp , dp2; 
    vector<int> anss; 

    void dfs(int node , int par , vector<vector<int>> &g)
    {
        int ans = 0 ; 
        for(auto & i : g[node])
        {
            if(i!=par)
            {
                dfs(i,node , g);
                ans = max(ans, dp[i] + (i %2 == 0 ? 2 : 1)); 
            }
        }
        dp[node] = ans ; 
    }

    void dfs2(int node , int par , vector<vector<int>> &g)
    {
        multiset<int> vals ; 
        for(auto & i : g[node])
        {
            if(i!=par)
                vals.insert(dp[i] + (i %2 == 0 ? 2 : 1)) ;
        }
        vals.insert(dp2[node]) ; 
        for(auto & i : g[node])
        {
            if(i!=par)
            {
                vals.erase(vals.find(dp[i] + (i %2 == 0 ? 2 : 1)));
                int mxx = ( node %2 == 0 ? 2 : 1) ; 
                if(vals.size()>0)
                    mxx+= (*vals.rbegin()) ;
                // cout<<"i : "<<i<<" mxx : "<<mxx<<" dp[i] : "<<dp[i]<<"\n" ; 
                // mxx = max(mxx , anss[node]) ; 
                dp2[i] = mxx ; 
                anss[i] = max(dp[i] , mxx); 
                vals.insert((dp[i] + (i %2 == 0 ? 2 : 1)));

            }
        }
         for(auto & i : g[node])
        {
            if(i!=par)
                dfs2(i,node,g);
        }

    }

    vector<int> timeTaken(vector<vector<int>>& edges) {
        dp.clear();
        int n = edges.size() ; 
        dp.resize(n+1,0);
         dp2.clear();
        dp2.resize(n+1,0);
        anss.clear();
        anss.resize(n+1,0);

        vector<vector<int>> g(n+1);

        for(auto & i : edges)
        {
            g[i[0]].push_back(i[1]);
            g[i[1]].push_back(i[0]) ;
        }
        dfs(0,-1,g);
        dfs2(0,-1,g);
        anss[0] = dp[0] ; 
        // for(auto & i : dp)
        //     cout<<i<<" ";
        // cout<<"\n" ; 
        return anss ; 

    }
};
