class Solution {
public:
    int fir[100005],sec[100005],ans[100005];
    vector<vector<int>> g;
    unordered_map<int,int> ew[100005];
    int getew(int temp){
        if(temp&1) return 1;
        return 2;
    }
    void dfs(int r,int p){
        for(auto child:g[r]){
            if(child == p) continue;
            dfs(child,r);
            if(fir[child] + getew(child) > fir[r]){
                sec[r] = fir[r];
                fir[r] = fir[child] + getew(child);
            }
            else if(fir[child] + getew(child)>sec[r]){
                sec[r] = fir[child] + getew(child);
            }
        }
    }
    void dfs2(int r,int p,int out){
        ans[r] = max(out,fir[r]);
        for(int i:g[r]){
            if(i==p) continue;
            if(fir[i] + getew(i)==fir[r]){
                dfs2(i,r,max(sec[r],out) + getew(r));
            }
            else{
                dfs2(i,r,ans[r] + getew(r));
            }
        }
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n=edges.size();
        g.resize(n+1);
        for(auto it:edges){
            g[it[0]].push_back(it[1]);
            g[it[1]].push_back(it[0]);
            // if(it[1]&1){
            //     ew[it[0]][it[1]] = 1;
            // }
            // else ew[it[0]][it[1]] = 2;
            // if(it[0]&1){
            //     ew[it[1]][it[0]] = 1;
            // }
            // else{
            //     ew[it[1]][it[0]] = 2;
            // }
        }
        dfs(0,0);
        // for(int i=0;i<n+1;++i){
        //     cout<<fir[i]<<' '<<sec[i]<<endl;
        // }
        dfs2(0,0,0);
        vector<int> res;
        for(int i=0;i<n+1;++i){
            //cout<<ans[i]<<endl;
            res.push_back(ans[i]);
        }
        return res;


    }
};
