class Solution {
public:
    void fun1(int node, int parent, vector<vector<int>> &adj, map<int,int> &map1)
    {
        int maxi = 0;
        for(auto it:adj[node])
        {
            if(it != parent)
            {
                fun1(it,node,adj,map1);
                maxi = max(maxi,map1[it]);
            }
        }
        if(node%2 == 0)
        {
            maxi += 2;
        }
        else
        {
            maxi += 1;
        }

        map1[node] = maxi;
    }
    void fun2(int node, int parent, vector<vector<int>> &adj, map<int,int> &map1,vector<int> &ans, int upper)
    {
        vector<int> v1;
        for(auto it:adj[node])
        {
            if(it != parent)
            {
                v1.push_back(map1[it]);
            }
        }
        v1.push_back(upper);
        sort(v1.begin(),v1.end());
        ans[node] = v1.back();

            for(auto it:adj[node])
             {
                 if(it != parent)
                 {
                     int val;
                     if(v1.back() == map1[it])
                     {
                         val = v1[v1.size()-2];
                     }
                     else
                     {
                         val = v1.back();
                     }
                     val += (node%2 == 0 ? 2 : 1);
                     fun2(it,node,adj,map1,ans,val);
                 }
             }
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n = edges.size()+1;

        vector<vector<int>> adj(n+1);
        for(auto it:edges)
        {
            adj[it[0]].push_back(it[1]);
            adj[it[1]].push_back(it[0]);
        }

        map<int,int> map1;
        fun1(0,-1,adj,map1);

        vector<int> ans(n);
        fun2(0,-1,adj,map1,ans,0);
        return ans;
    }
};
