class Solution:
    def timeTaken(self, edges: List[List[int]]) -> List[int]:
        n = len(edges) + 1
        d1 = defaultdict(set)
        for start, end in edges:
            d1[start].add(end)
            d1[end].add(start)
        d = defaultdict(list)
        hs = set()
        hs.add(0)
        cur = {0}
        while len(cur) > 0:
            ns = set()
            for c in cur:
                for cc in d1[c]:
                    if cc not in hs:
                        ns.add(cc)
                        d[c].append(cc)
                        hs.add(cc)
            cur = ns
        d3 = defaultdict(list)

        def dfs(node):
            if len(d[node]) > 0:
                m = 0
                for child in d[node]:
                    a = dfs(child)
                    if child % 2 == 0:
                        a += 2
                    else:
                        a += 1
                    m = max(m, a)
                    d3[node].append(a)
                return m
            else:
                return 0
        dfs(0)

        ans = [0] * n
        d4 = defaultdict(tuple)
        for key,value in d3.items():
            vv = sorted(value)
            if len(vv) == 0:
                d4[key] = (0,0)
            elif len(vv) == 1:
                d4[key] = (vv[0] , 0)
            else:
                d4[key] = (vv[-1] ,vv[-2])
        # print(d3)
        # print(d4)
        def dfs1(node, p):
            # print(node,p)
            if len(d[node]) > 0:
                ans[node] = max(p, d4[node][0])
                for i, child in enumerate(d[node]):
                    if d4[node][0] == d3[node][i]:
                        pp = max(p, d4[node][1])
                    else:
                        pp = max(p, d4[node][0])
                    if node % 2 == 0:
                        dfs1(child, pp + 2)
                    else:
                        dfs1(child, pp + 1)
            else:
                ans[node] = p
        dfs1(0,0)
        return ans
