class Solution {
public:
    vector<int> timeTaken(vector<vector<int>>& edges) {
        long long n=edges.size()+1;
        vector<vector<long long>>adjlist(n,vector<long long>());
        for(long long r=0;r<edges.size();r++)
        {
            adjlist[edges[r][0]].push_back(edges[r][1]);
             adjlist[edges[r][1]].push_back(edges[r][0]);
        }
        vector<long long >e(n);
        function<void(long long,long long,long long)>efs=[&](long long node,long long parent,long long value)
        {
            for(long long r=0;r<adjlist[node].size();r++)
            {
                if(adjlist[node][r]==parent)
                {

                }
                else
                {
                    efs(adjlist[node][r],node,value);
                }
            }
            for(long long r=0;r<adjlist[node].size();r++)
            {
                if(adjlist[node][r]==parent)
                {

                }
                else
                {
                    if(adjlist[node][r]%2==1)
                    {
                        e[node]=max(e[node],1+e[adjlist[node][r]]);
                    }
                    else
                    {
                        e[node]=max(e[node],2+e[adjlist[node][r]]);
                    }

                }
            }
        };
        efs(0,-1,0);

        vector<long long>ee(n);
        function<void(long long,long long,long long)>ifs=[&](long long node,long long parent,long long value)
        {
            if(parent==-1)
            {

            }
            else
            {
            if(parent%2==1)
               {
                ee[node]=max(ee[node],ee[parent]+1);
               }
               else
               {
                ee[node]=max(ee[node],ee[parent]+2);
               }
                if(parent%2==1)
               {
                ee[node]=max(ee[node],value+1);
               }
               else
               {
                ee[node]=max(ee[node],value+2);
               }
            }
            vector<vector<long long>>u;
            for(long long r=0;r<adjlist[node].size();r++)
            {

                if(adjlist[node][r]==parent)
                {

                }
                else
                {
                    if(adjlist[node][r]%2==1)
                    {
                        u.push_back({e[adjlist[node][r]]+1,adjlist[node][r]});
                    }
                    else
                    {
                        u.push_back({e[adjlist[node][r]]+2,adjlist[node][r]});
                    }
                }
            }

                sort(u.begin(),u.end());
                for(long long r=0;r<adjlist[node].size();r++)
                {
                if(adjlist[node][r]==parent)
                {

                }
                else
                {
                    if(adjlist[node][r]==u[u.size()-1][1])
                    {
                        if(u.size()==1)
                        {
                            ifs(adjlist[node][r],node,0);
                        }
                        else
                        {
                            ifs(adjlist[node][r],node,u[u.size()-2][0]);
                        }
                    }
                    else
                    {
                        ifs(adjlist[node][r],node,u[u.size()-1][0]);
                    }                    
                }
                }
        };
        ifs(0,-1,0);
        vector<int>times(n);
        for(long long r=0;r<n;r++)
        {
            times[r]=max(e[r],ee[r]);
        }
        return times;
    }
};
