class Solution {
public:
    void findans(int i,vector<vector<int>>& adj,vector<vector<int>>& v,int par,vector<int>& ans,int parl){
        ans[i]=max(parl,v[i][0]);
        int e=1;
        if(i%2==0) e=2; 
        for(auto a:adj[i]){
            if(a==par) continue;
            if(a==v[i][1]) findans(a,adj,v,i,ans,max(parl,v[i][2])+e);
            else findans(a,adj,v,i,ans,max(parl,v[i][0])+e);
        }
    }
    int find1(int i,vector<vector<int>>& adj,vector<vector<int>>& v,int par){
        int max1=0,maxi=-1,max2=0;
        for(auto a:adj[i]){
            if(a==par) continue;
            int z=find1(a,adj,v,i);
            if(z>max1){
                max2=max1;
                max1=z;
                maxi=a;
            }
            else if(z>max2) max2=z;
        }
        v[i][0]=max1;v[i][1]=maxi;v[i][2]=max2;
        if(i%2==0) return max1+2;
        else return max1+1;
    }
    vector<int> timeTaken(vector<vector<int>>& edges) {
        int n=edges.size()+1;
        vector<vector<int>> adj(n);
        for(auto a:edges){
            adj[a[0]].push_back(a[1]);
            adj[a[1]].push_back(a[0]);
        }
        vector<vector<int>> v(n,vector<int>(3));
        find1(0,adj,v,-1);
        vector<int> ans(n);
        findans(0,adj,v,-1,ans,0);
        return ans;
    }
};
